    fun getMovieList(): ArrayList<Movies> {
        val movieList = ArrayList<Movies>()
        for (data in movies) {
            val movie = Movies(
                data.id,
                data.name,
                data.description,
                data.photo,
                data.releaseDate,
                data.photoCover
            )
            movieList.add(movie)
        }
        return movieList
    }
